mapper.select:
SELECT prod_name, prod_id, prod_price 
FROM product
WHERE prod_id = #{prod_id}

mapper.insert:
INSERT INTO purchase (
    order_num, prod_id, member_id, qty, purc_request, purc_date
) VALUES (
    #{order_num},
    #{prod_id},
    #{member_id},
    #{qty},
    #{purc_request},
    #{purc_date}
)

mapper.update:
UPDATE product 
SET prod_stock = prod_stock - #{qty} 
WHERE prod_id = #{prod_id}
